import React, { useEffect, useLayoutEffect} from 'react';
import '../../../assets/scss/core.scss';
import { useOnTop } from '../../../Hooks/useOnTop';
import {useViewport} from '../../../Hooks/useViewport'

const BasicNavbar = () => { 

    let navClass = "";
    useLayoutEffect(() => {
        const onScroll = () => {
          let scrollPosition = window.scrollY;
           navClass = scrollPosition > 10000 ? 
          ("navbar is-inline-flex is-transparent no-shadow no-background is-landing is-hidden-mobile")
          :
          ("navbar is-inline-flex is-transparent  is-landing is-hidden-mobile")
        };
    
        window.addEventListener("scroll", onScroll);
        return () => window.removeEventListener("scroll", onScroll);
        /* 
           remove the event listener in the cleanup function 
           to prevent memory leaks
        */
      }, []);


    return (
    // Remove no-Shadow and no-Background classes when scroll down
    <div className={navClass}>
        <div className="container is-fluid">
            <div className="navbar-brand">
                <a href="/" className="navbar-item">
                    <img className="logo" src="assets/img/logo/friendkit-bold.svg" alt="" />
                </a>
            </div>
            <div className="navbar-menu">
                <div className="navbar-start">
                    <div className="navbar-item">
                        <a href="#buy">Buy</a>
                    </div>
                    <div className="navbar-item">
                        <a href="#demos-section">Demos</a>
                    </div>
                    <div className="navbar-item">
                        <a>Docs</a>
                    </div>
                </div>
                <div className="navbar-end">
                    <div className="navbar-item">
                        <div className="navbar-item">
                            <a href="/login.html">Login</a>
                        </div>
                        <a id="signup-button" href="/signup.html" className="button is-cta is-solid primary-button raised">
                            Sign Up
                        </a>
                    </div>
                </div>

            </div>
        </div>
    </div>
)}

const NavbarMobile = () => (
    // Remove no-Shadow and no-Background classes when scroll down
    <nav className="navbar mobile-navbar is-landing is-hidden-desktop no-shadow no-background" aria-label="main navigation">
        {/* <!-- Brand --> */}
        <div className="navbar-brand">
            <a className="navbar-item" href="/">
                <img className="dark-mobile-logo" src="assets/img/logo/friendkit.svg" alt="" />
                <img className="light-mobile-logo" src="assets/img/logo/friendkit-white.svg" alt="" />
            </a>

            {/* <!-- Mobile menu toggler icon --> */}
            <div className="navbar-burger">
                <span></span>
                <span></span>
                <span></span>
            </div>
        </div>
        {/* <!-- Navbar mobile menu --> */}
        <div className="navbar-menu">
            {/* <!-- Account --> */}
            <div className="navbar-item has-dropdown">
                <div className="navbar-link">
                    <img src="https://via.placeholder.com/150x150" data-demo-src="assets/img/avatars/avatar-w.png" alt="" />
                    <span className="is-heading">Guest</span>
                </div>

                {/* <!-- Mobile Dropdown --> */}
                <div className="navbar-dropdown">
                    <a href="#" className="navbar-item">Buy</a>
                    <a href="#demos-section" className="navbar-item">Demos</a>
                    <a href="https://docs.friendkit.cssninja.io" className="navbar-item">Docs</a>
                    <a href="login.html" className="navbar-item">Login</a>
                    <a href="signup.html" className="button is-fullwidth is-solid accent-button">Sign Up</a>
                </div>
            </div>
        </div>
    </nav>

)

export const Navbar = () => {

    const { width } = useViewport();
    const breakpoint = 620;

    useEffect((width, breakpoint) => (width < breakpoint ? <NavbarMobile /> : <BasicNavbar />) )

    return width < breakpoint ? <NavbarMobile /> : <BasicNavbar />;

}
