import React, { useEffect, useLayoutEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../../../assets/scss/core.scss';
import { useOnTop } from '../../../Hooks/useOnTop';
import { useViewport } from '../../../Hooks/useViewport'

const BasicNavbar = () => {

    const [navScroll, setNavScroll] = useState(false)

    let  navClass = ""
    useLayoutEffect(() => {
        const onScroll = () => {
            const scrollPos = window.scrollY;
            console.log('scrollPos', scrollPos)
            if (scrollPos > 10) {
                console.log('true')
                setNavScroll((true));
                navClass = ("navbar is-inline-flex is-transparent no-shadow no background is-landing is-hidden-mobile")
            }else {
                console.log('false')
                setNavScroll((false))
                navClass = ("navbar is-inline-flex is-transparent is-landing is-hidden-mobile")

            }

            console.log('scrollPos é ', navScroll? 'true' : 'false')

        };
        window.addEventListener("scroll", onScroll);
        return () => window.removeEventListener("scroll", onScroll);
    }, []);




    return (
        // Remove no-Shadow and no-Background classes when scroll down
        <>
        <div className="navbar is-inline-flex is-transparent is-landing is-hidden-mobile">
            <div className="container is-fluid">
                <div className="navbar-brand">
                    <Link to="/" className="navbar-item">
                        <img className="logo" src="assets/img/logo/friendkit-bold.svg" alt="" />
                    </Link>
                </div>
                <div className="navbar-menu">
                    <div className="navbar-start">
                        <div className="navbar-item">
                            <Link to="#buy">Buy</Link>
                        </div>
                        <div className="navbar-item">
                            <Link to="#demos-section">Demos</Link>
                        </div>
                        <div className="navbar-item">
                            <Link>Docs</Link>
                        </div>
                    </div>
                    <div className="navbar-end">
                        <div className="navbar-item">
                            <div className="navbar-item">
                                <Link to="/login">Login</Link>
                            </div>
                            <Link id="signup-button" to="/signup" className="button is-cta is-solid primary-button raised">
                                Sign Up
                        </Link>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    </>
    )
}

const NavbarMobile = () => (
    // Remove no-Shadow and no-Background classes when scroll down
    <nav className="navbar mobile-navbar is-landing is-hidden-desktop no-shadow no-background" aria-label="main navigation">
        {/* <!-- Brand --> */}
        <div className="navbar-brand">
            <Link className="navbar-item" to="/">
                <img className="dark-mobile-logo" src="assets/img/logo/friendkit.svg" alt="" />
                <img className="light-mobile-logo" src="assets/img/logo/friendkit-white.svg" alt="" />
            </Link>

            {/* <!-- Mobile menu toggler icon --> */}
            <div className="navbar-burger">
                <span></span>
                <span></span>
                <span></span>
            </div>
        </div>
        {/* <!-- Navbar mobile menu --> */}
        <div className="navbar-menu">
            {/* <!-- Account --> */}
            <div className="navbar-item has-dropdown">
                <div className="navbar-link">
                    <img src="https://via.placeholder.com/150x150" data-demo-src="assets/img/avatars/avatar-w.png" alt="" />
                    <span className="is-heading">Guest</span>
                </div>

                {/* <!-- Mobile Dropdown --> */}
                <div className="navbar-dropdown">
                    <Link to="#" className="navbar-item">Buy</Link>
                    <Link to="#demos-section" className="navbar-item">Demos</Link>
                    <Link to="https://docs.friendkit.cssninja.io" className="navbar-item">Docs</Link>
                    <Link to="login" className="navbar-item">Login</Link>
                    <Link to="signup" className="button is-fullwidth is-solid accent-button">Sign Up</Link>
                </div>
            </div>
        </div>
    </nav>

)

export const Navbar = () => {

    const { width } = useViewport();
    const breakpoint = 620;

    useEffect((width, breakpoint) => (width < breakpoint ? <NavbarMobile /> : <BasicNavbar />))

    return width < breakpoint ? <NavbarMobile /> : <BasicNavbar />;

}
