import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import FadeIn from 'react-fade-in';
import navLogo from '../../assets/img/logo/friendkit-bold.svg'
import { Smile, Image, User,  Lock, Flag } from 'react-feather';
import {ProcessPanel} from './processPanel';





function FakeNav() {
    return (
        <div className="fake-nav">
            <Link href="/" className="logo">
                <img src={navLogo} width="112" height="28" alt="" />
            </Link>
        </div>
    )
}

const ProcessBar = (props) => {
    
const isCurrent = "is-active is-current"
const isActive = "is-active"

    let width = 125*props.stage;
    return(
    <div className="process-bar-wrap">
        <div className="process-bar">
            <div className="progress-wrap">
                <div className="track"></div>
                <div className="bar" style={{width: width}}></div>
                <div id="step-dot-1" className={`dot is-first ${props.stage == 1 ?isCurrent : isActive}`}>
                    <Smile/>
                </div>
                <div id="step-dot-2" className={`dot is-second ${props.stage == 1 ?isCurrent : isActive}`}>
                    <User/>
                </div>
                <div id="step-dot-3" className={`dot is-third ${props.stage == 1 ?isCurrent : isActive}`}>
                    <Image/>
                </div>
                <div id="step-dot-4" className={`dot is-fourth ${props.stage == 1 ?isCurrent : isActive}`}>
                    <Lock/>
                </div>
                <div id="step-dot-5" className={`dot is-fifth`}>
                    <Flag/>
                </div>
            </div>
        </div>
    </div>
)}

export default function SignUp() {

const [stage, setStage] = useState(0);


    return (
        <div className="signup-wrapper">
            <FakeNav />
            <ProcessBar stage={stage}/>
            <ProcessPanel stage={stage} setStage={setStage}/>
            <h1>SignUp</h1>
        </div>
    )
}
